var popup = null;
var popupName = null;

function copyToClipboard(element) {
    document.querySelector(element).select();
    document.execCommand('copy');
}

/**
 * Opens a single popup and reuses it for subsequent calls
 *
 * @param string url
 * @param string name
 * @param object features
 */
function openPopup(url, name, features) {

    if (popup == null || popup.closed || popupName === name) {
        popup = window.open(url, name, buildFeatureString(features));
    } else {
        popup.focus();
    };
}

/**
 * Convert feature object to string
 *
 * @param object features
 * @returns string
 */
function buildFeatureString(features) {
    if (typeof features !== 'object' || features === null)
        return '';

    var result = [];

    if (features.position && features.position === 'center') {
        centerPopup(features);
    }

    for (name in features) {
        let value = features[name];
        result.push(name + "=" + value)
    }

    return result.join(',');
}

/**
 * Sets positioning feature parameters to display the popup in the center of the parent browsing context
 *
 * @param array object
 */
function centerPopup(features) {
    let width = features.width;
    let height = features.height;
    if (width)
        features.left = window.top.outerWidth / 2 + window.top.screenX - ( width / 2);
    if (height)
        features.top = window.top.outerHeight / 2 + window.top.screenY - ( height / 2);

    delete features.position;
}

